]

const Testimonial = ()=>{


    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 3,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 3000,
        cssEase: "linear",
    };

return(
    <div className="Testimonial-sections">
        <div className="CustomTitle-Testimonials">
             <h1 className="Testimonial-Title">What User says</h1>
             <p className="Testimonial-Description">Testimonials that speak louder than words! Customer stories that light up our day.</p>
        </div>
            <Slider {...settings}>
            { 
            sliderContents.map((item,index)=>(
                <div className={`Customer-Reviews-Slide testimonial-item zigzag-${index % 2 === 0 ? 'right' : 'left'}`} >
                <div className="testimonial-slide-inner">
                   <p>{item.text}</p>
                   <h4>{item.name}</h4>
                   <h5>{item.company}</h5>
               </div>
           </div>
            ))

            }

            </Slider>

    </div>
)

}

export default Testimonial;
