import React, { useEffect, useRef, useState } from "react";
import Slider from "react-slick";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import './ScrollSliderHorizontal.css'; // Custom CSS for styling

const ScrollSliderHorizontal = () => {
  const [currentSlide, setCurrentSlide] = useState(0);
  const sliderRef = useRef(null); // To access the Slider component

  const settings = {
    dots: true,
    infinite: true, // Enable infinite looping
    slidesToShow: 5, // Show 5 slides at a time
    slidesToScroll: 1, // Scroll 1 slide at a time
    speed: 1500,
    centerMode: false, // No need for centering with 5 slides
    beforeChange: (index, newIndex) => setCurrentSlide(newIndex), // Capture current slide index
  };

  const slides = [
    "Slide 1",
    "Slide 2",
    "Slide 3",
    "Slide 4",
    "Slide 5",
    "Slide 6",
    "Slide 7",
    "Slide 8",
    "Slide 9",
    "Slide 10"
  ];

  // Scroll Event Handler
  useEffect(() => {
    const handleScroll = (e) => {
      if (e.deltaY > 0 || e.deltaX > 0) {
        // Scroll right, move to the next slide
        sliderRef.current.slickNext();
      } else if (e.deltaY < 0 || e.deltaX < 0) {
        // Scroll left, move to the previous slide
        sliderRef.current.slickPrev();
      }
    };

    window.addEventListener('wheel', handleScroll);

    return () => {
      window.removeEventListener('wheel', handleScroll);
    };
  }, []);

  return (
    <div style={{ width: "80%", margin: "0 auto" }}>
      <h2>Scroll to Slide Horizontally with 5 Slides</h2>
		{console.log(currentSlide)}
      <Slider ref={sliderRef} {...settings}>
        {slides.map((slide, index) => {
          // Determine if the slide is odd or even based on its index
			 console.log(( index % 2 === currentSlide % 2 ), "========", index )
          const slideClass = ( index % 2 === currentSlide % 2 ) ? 'slide-odd' : 'slide-even';
          const isVisible = index === currentSlide ? 'visible' : '';

          return (
            <div key={index} className={`slide ${slideClass}`}>
              <h3>{slide}</h3>
            </div>
          );
        })}
      </Slider>
    </div>
  );
};

export default ScrollSliderHorizontal;
