import React from 'react';
import Slider from 'react-slick';

const sliderContent = [
    {
        name: "John Doe",
        text: "This is an amazing product! Highly recommend.",
        company: "Company A"
    },

]

const Testimonial = ()=>{


    const settings = {
        dots: true,
        infinite: true,
        speed: 500,
        slidesToShow: 1,
        slidesToScroll: 1,
    };

return(
    <div className="Testimonial-sections">
        <div className="CustomTitle-Testimonials">
             <h1 className="Testimonial-Title">What User says</h1>
             <p className="Testimonial-Description">Testimonials that speak louder than words! Customer stories that light up our day.</p>
        </div>
        <ul className="Testimonials-Reviews-Section">
            <Slider {...settings}>
            { 
            sliderContent.map((sliderContent,index)=>(
                <li className="Customer-Reviews-Slide" key={index}>
                     <div className="testimonial-slide">
                        <p>{sliderContent.text}</p>
                        <h4>{sliderContent.name}</h4>
                        <h5>{sliderContent.company}</h5>
                    </div>
                </li>

            ))

            }

            </Slider>


        </ul>

    </div>
)

}

export default Testimonial;
